# cord1 and cord2 must be passed as a tuple


def getDirectionList(cord1, cord2):
    directionsCords = gmaps.directions(cord1, cord2)
    return directionsCords


def getAddressFromCords(cord1):
    addressRes = gmaps.reverse_geocode(cord1)
    addr = addressRes[0]['formatted_address']
    ind = addr.find(",")
    add_res = addr[ind+2:]
    return add_res


